mapper.update:
-- SubtitleDao.updateSttlNum
UPDATE STTL.TSSCM00030
SET    STTL_NUM = #{sttlNum}
     , UPD_DTM  = NOW()
     , UPD_USR_ID = #{usrId}
WHERE  USR_ID  = #{usrId}
AND    STTL_NM = #{sttlNm}
AND    STTL_CD = #{sttlCd}

-- SubtitleDao.updateReviewCnt
UPDATE STTL.TSSCM00040
SET    REVIEW_CNT = REVIEW_CNT + 1
     , UPD_DTM  = NOW()
     , UPD_USR_ID = #{usrId}
WHERE  USR_ID  = #{usrId}
AND    STTL_NM = #{sttlNm}
AND    SERIAL_NO = #{serialNo}

-- SubtitleDao.updateReviewCd
UPDATE STTL.TSSCM00040
SET    REVIEW_CD = #{reviewCd}
     , REVIEW_CNT = REVIEW_CNT + 1
     , UPD_DTM  = NOW()
     , UPD_USR_ID = #{usrId}
WHERE  USR_ID  = #{usrId}
AND    STTL_NM = #{sttlNm}
AND    SERIAL_NO = #{serialNo}
