mapper.update:
-- UsrDao.updateUsr
UPDATE STTL.TSYUR00010
SET   USR_ID     = #{usrEmail}
    , USR_EMAIL  = #{usrEmail}
    , USR_PW     = #{usrPw}
    , USR_NM     = #{usrNm}
    , USR_AGE    = CAST(#{usrAge} AS INT)
    , USE_YN     = CASE WHEN #{useYn} = 'true' THEN 'Y' ELSE 'N' END
    , UPD_DTM    = curdate()
    , UPD_USR_ID = #{updUsrId}
WHERE  USR_ID = #{usrEmail}

-- SubtitleDao.updateSttlNum
UPDATE STTL.TSYUR00020
SET    FLANG_CD = #{flangCd}
     , MLANG_CD = #{mlangCd}
     , UPD_DTM  = NOW()
     , UPD_USR_ID = #{usrId}
WHERE  USR_ID  = #{usrId}
